mapper.select:
SELECT
,
    vc.customer_name,
    vc.customer_code,
    vc.first_order_date,
    vc.adjusted_first_order_date,
    vc.transfer_status as is_transfer,
    vso.amount
FROM v_commission_record
    left join v_customer vc on v_commission_record.customer_id = vc.customer_id
    left join v_sales_outbound vso on v_commission_record.sales_bill_no = vso.bill_no



        AND (
        -- 条件1：业务员自身记录（所有提成类型）
        v_commission_record.salesperson_id = #{queryForm.salespersonId}

        OR

        -- 条件2：管理提成记录（上级为当前业务员）
        (
        v_commission_record.current_parent_id = #{queryForm.salespersonId}
        AND
        v_commission_record.commission_type = 2  
        )
        )


        AND v_commission_record.salesperson_id = #{queryForm.salespersonId}


        AND v_commission_record.current_parent_id = #{queryForm.salespersonId}



        AND INSTR(vc.customer_name,#{queryForm.customerName})


        AND v_commission_record.is_customs_declaration = #{queryForm.isCustomsDeclaration}



        AND v_commission_record.order_date >= #{queryForm.orderDateBegin}


        AND v_commission_record.order_date <= #{queryForm.orderDateEnd}


        AND v_commission_record.commission_type = #{queryForm.commissionType}


        AND INSTR(v_commission_record.sales_order_bill_no,#{queryForm.salesBillNo})


        And


                order_date BETWEEN #{dateVO.startDate} AND #{dateVO.endDate}




order by v_commission_record.order_date desc, v_commission_record.sales_bill_no

SELECT , vc.customer_name, vc.customer_code, vc.first_order_date, vc.adjusted_first_order_date, vc.transfer_status as is_transfer, vso.amount, vso_order.order_type, vrd.material_name, vrd.sale_unit, vrd.sale_quantity FROM v_commission_record left join v_customer vc on v_commission_record.customer_id = vc.customer_id left join v_sales_outbound vso on v_commission_record.sales_bill_no = vso.bill_no left join v_sales_order vso_order on vso_order.bill_no = v_commission_record.sales_order_bill_no LEFT JOIN v_receivables_details vrd ON v_commission_record.receive_bill_no = vrd.origin_bill_no
AND v_commission_record.salesperson_id = #{queryForm.salespersonId}
AND INSTR(vc.customer_name,#{queryForm.customerName})
AND v_commission_record.is_customs_declaration = #{queryForm.isCustomsDeclaration}
AND v_commission_record.order_date >= #{queryForm.orderDateBegin}
AND v_commission_record.order_date <= #{queryForm.orderDateEnd}
AND v_commission_record.commission_type = #{queryForm.commissionType}
AND INSTR(v_commission_record.sales_bill_no,#{queryForm.salesBillNo})
And
order_date BETWEEN #{dateVO.startDate} AND #{dateVO.endDate}
order by v_commission_record.order_date desc, v_commission_record.sales_bill_no
