mapper.select:
SELECT

FROM v_commission_rule



        AND v_commission_rule.customer_group = #{queryForm.customerGroup}



        AND v_commission_rule.transfer_status = #{queryForm.transferStatus}



        AND v_commission_rule.commission_type = #{queryForm.commissionType}


order by v_commission_rule.transfer_status asc,
    v_commission_rule.customer_group asc,
v_commission_rule.commission_type asc

