mapper.select:
SELECT , vs.deleted_flag, -- 应收单编号列表（去重） GROUP_CONCAT(DISTINCT vr.bill_no ORDER BY vr.bill_no SEPARATOR ', ') as receive_bill_no, -- 应收金额汇总（对不同的应收单去重后求和） ( SELECT SUM(DISTINCT vr2.amount) FROM v_f_sales vfs2 LEFT JOIN v_f_outbound_rel vfo2 ON vfo2.f_sales_no = vfs2.bill_no LEFT JOIN v_sales_outbound vso2 ON vso2.bill_no = vfo2.outbound_no LEFT JOIN v_receivables vr2 ON vso2.bill_no = vr2.origin_bill_no WHERE vfs2.origin_bill_no = vso_order.bill_no AND vr2.bill_no IS NOT NULL ) as receive_amount FROM v_sales_order vso_order left join v_f_sales vfs ON vfs.origin_bill_no = vso_order.bill_no left join v_f_outbound_rel vfo on vfo.f_sales_no = vfs.bill_no left join v_sales_outbound vso on vso.bill_no = vfo.outbound_no left JOIN v_receivables vr ON vso.bill_no = vr.origin_bill_no left JOIN v_customer vc ON vc.customer_code = vso_order.customer_code left JOIN v_salesperson vs ON vs.salesperson_code = vso_order.salesperson_code left JOIN v_salesperson_level vsl ON vs.salesperson_level_id = vsl.salesperson_level_id left JOIN v_salesperson pvs ON vs.parent_id = pvs.id left JOIN v_salesperson_level pvsl ON pvs.salesperson_level_id = pvsl.salesperson_level_id left JOIN t_department d ON d.department_id = vs.department_id
AND INSTR(vc.customer_name,#{queryForm.customerName})
AND INSTR(vs.salesperson_name,#{queryForm.salespersonName})
AND INSTR(vso_order.bill_no,#{queryForm.billNo})
AND INSTR(d.name, #{queryForm.departmentName})
AND DATE_FORMAT(vso_order.order_date, '%Y-%m-%d') >= #{queryForm.orderDateBegin}
AND DATE_FORMAT(vso_order.order_date, '%Y-%m-%d') <= #{queryForm.orderDateEnd}
And vc.customer_name != #{excludeForm.customerName}
And vs.deleted_flag != #{excludeForm.deletedSalesmanFlag}
And vs.disabled_flag != #{excludeForm.disabledSalesmanFlag}
GROUP BY vso_order.bill_no order by vso.sales_bound_date DESC
